import React from 'react';

class ComponentDidMount extends React.Component {
  constructor(props) {
    super(props);
    this.state = { counter: 0 };
  }

  componentDidMount() {
    this.setState({ counter: 1 });
  }

  render() {
    return <div>{this.state.counter}</div>;
  }
}

export default ComponentDidMount;
