import React from 'react';

class Listener extends React.Component {
  render() {
    return (
      <div className={this.props.main}>
        <p>component taking in other component through render props</p>
      </div>
    );
  }
}

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      className: "card",
    };
  }

  handleClick() {
    this.setState({
      className: "card",
    });
  }

  render() {
    return (
      <div>
        <div className={this.state.className}>
          <p>main component</p>
        </div>
        <button onClick={this.handleClick} style={{ marginTop: "1rem" }}>
          Set the state
        </button>
        {this.props.render(this.state)}
      </div>
    );
  }
}

export default class RenderProps extends React.Component {
  render() {
    return (
      <div>
        <h2>Render props:</h2>
        <Main render={(main) => <Listener main={main} />} />
      </div>
    );
  }
}
