import React from 'react';

const OptionOne = () => <h1>Hello 1</h1>;
const OptionTwo = () => <h1>Hello 2</h1>;

const InlineConditionalRendering = (props) => (
  <div>{props ? <h1>First message</h1> : <h1>Second message</h1>}</div>
);

class ConditionalRendering extends React.Component {
  showMessage() {
    if (true) {
      return <OptionOne />;
    } else {
      return <OptionTwo />;
    }
  }

  render() {
    return (
      <div>
        {this.showMessage()} <InlineConditionalRendering props={true} />
      </div>
    );
  }
}

export default ConditionalRendering;
