import { useRef } from "react";

export default function UseRef() {
  const pElement = useRef(null);
  const onButtonClick = () => {
    pElement.current.innerHTML = "hello";
  };

  return (
    <div>
      <h1>useRef:</h1>
      <p ref={pElement}>hi</p>
      <button onclick={onButtonClick}>change text</button>
    </div>
  );
}
