import React from "react";

class Home extends React.Component {
    getFundExamples() {
        return [
            "jsx",
            "functionalComponent",
            "classComponent",
            "state",
            "props",
            "conditionalRendering",
            "componentDidMount",
            "componentDidUpdate",
            "componentWillUnmount",
            "shouldComponentUpdate",
            "getDerivedStateFromProps",
            "getSnapshotBeforeUpdate",
            "getDerivedStateFromError",
            "mapKey",
            "compositionVsInheritance",
            "useState",
            "useEffect",
        ];
    }
    getAdvExamples() {
        return [
            "customHook",
            "useCallback",
            "useRef",
            "useMemo",
            "useReducer",
            "useContext",
            "context",
            "refs",
            "renderProps",
            "codeSplitting",
            "higherOrderComponents",
            "portals",
            "errorBoundaries",
            "fiberArchitecture",
        ];
    }

    getLink(title) {
        const href = `/${title}`;
        return ( <
            a href = { href } >
            <
            button > { title } < /button> <
            /a>
        );
    }

    render() {
        return ( <
            div >
            <
            h1 > React Roadmap < /h1> <
            hr / >
            <
            h2 > Fundamental: < /h2> { this.getFundExamples().map((ex, i) => this.getLink(ex, i)) } <
            hr / >
            <
            h2 > Advanced: < /h2> { this.getAdvExamples().map((ex, i) => this.getLink(ex, i)) } <
            /div>
        );
    }
}

export default Home;
