import React from 'react';

class GetSnapshotBeforeUpdate extends React.Component {
  constructor(props) {
    super(props);
    this.titleRef = React.createRef();
  }

  getSnapshotBeforeUpdate(prevProps, prevState) {
    const title = this.titleRef.current;
    if (title.props !== prevProps.props) {
      return prevProps.props;
    }
    return title.color;
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (snapshot !== null) {
      this.titleRef.current.style.color = "red";
    }
  }

  render() {
    return (
      <div>
        <h1>getSnapshotBeforeUpdate:</h1>
        <p ref={this.titleRef} style={{ color: "purple" }}>
          {this.props.props}
        </p>
      </div>
    );
  }
}

export default GetSnapshotBeforeUpdate;
