import React from "react";

class ListEntry extends React.Component {
    constructor(props) {
        super(props);
        this.btn = React.createRef();
        this.focusBtn = React.createRef();
    }

    focusBtn() {
        this.btn.current.focus();
    }

    render() {
        const styles = {
            entry: {
                width: "300px",
                height: "100px",
                backgroundColor: "#dbdbdb",
                padding: "1rem",
                margin: "1rem",
                borderRadius: "1rem",
            },
        };

        return ( <
            div onClick = { this.focusBtn }
            style = { styles.entry } >
            <
            p > { this.props.firstName } { this.props.lastName } <
            /p> <
            button ref = { this.btn } > more info < /button> <
            /div>
        );
    }
}

class Refs extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const names = [{
                firstName: "Megan",
                lastName: "Smith",
            },
            {
                firstName: "Larry",
                lastName: "Hall",
            },
            {
                firstName: "Rosie",
                lastName: "Taylor",
            },
        ];

        return ( <
            div >
            <
            h1 > Refs: < /h1> {
                names.map((name) => ( <
                    ListEntry firsName = { name.firstName }
                    lastName = { name.lastName }
                    key = { key }
                    />
                ))
            } <
            /div>
        );
    }
}

export default Refs;
