import React from 'react';

class Home extends React.Component {
  getExamples() {
    return [
      "jsx",
      "functionalComponent",
      "classComponent",
      "state",
      "props",
      "conditionalRendering",
    ];
  }
  getLink(title) {
    const href = `/${title}`;
    return (
      <a href={href}>
        <button>{title}</button>
      </a>
    );
  }

  render() {
    return (
      <div>
        <h1>Examples:</h1>
        <h2>Fundamental:</h2>
        {this.getExamples().map((ex) => this.getLink(ex))}
        {this.getLink("conditionalRendering")}
        {this.getLink("componentDidMount")}
        {this.getLink("componentDidUpdate")}
        {this.getLink("componentWillUnmount")}
        {this.getLink("shouldComponentUpdate")}
        {this.getLink("getDerivedStateFromProps")}
        {this.getLink("getSnapshotBeforeUpdate")}
        {this.getLink("getDerivedStateFromError")}
        {this.getLink("mapKey")}
        {this.getLink("compositionVsInheritance")}
        {this.getLink("useState")}
        {this.getLink("useEffect")}
        <hr />
        <h2>Advanced:</h2>
      </div>
    );
  }
}

export default Home;
