import React from 'react';

class GetDerivedStateFromError extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return {
      hasError: true,
    };
  }

  render() {
    // if (this.state.hasError) {
    //   // You can render any custom fallback UI
    //   return <h1>Something went wrong.</h1>;
    // }

    return (
      <React.StrictMode>
        <div>
          <h1>static getDerivedStateFromError:</h1>
          {this.state.hasError ? <div>Some error</div> : <GFGComponent />}
        </div>
      </React.StrictMode>
    );
  }
}

export default GetDerivedStateFromError;

class GFGComponent extends React.Component {
  render() {
    return <h1>{this.state.heading}</h1>;
  }
}
