import React from "react";

class ErrorBoundary extends React.Component {
  state = {
    hasError: false,
    error: { message: "", stack: "" },
    info: { componentStack: "" },
  };

  static getDerivedStateFromError = (error) => {
    return { hasError: true };
  };

  componentDidCatch = (error, info) => {
    this.setState({ error, info });
  };

  render() {
    return this.state.hasError ? <p>Error detected</p> : this.props.children;
  }
}

const ChildComponent = () => <p>No errors in this child!</p>;

const ErrorChildComponent = () => {
  throw Error("Error message thrown in component");
};

const ErrorBoundaries = () => {
  return (
    <div>
      <h2>Error thrown, error message displayed:</h2>
      <ErrorBoundary>
        <ErrorChildComponent />
      </ErrorBoundary>

      <h2>No error thrown, children rendered:</h2>
      <ErrorBoundary>
        <ChildComponent />
      </ErrorBoundary>
    </div>
  );
};

export default ErrorBoundaries;
