import React from 'react';

class ConditionalRendering extends React.Component {
  greeting() {
    if (true) {
      return <h1>Hello 1</h1>;
    } else {
      return <h1>Hello 2</h1>;
    }
  }

  render() {
    return <div>{this.greeting()}</div>;
  }
}

export default ConditionalRendering;
