import React from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

import ClassComponent from './class-component';
import ComponentLifeCycle from './component-life-cycle';
import ConditionalRendering from './conditional-rendering';
import FunctionalComponent from './functional-component';
import Home from './home';
import MapKey from './map-key';
import PropsParent from './props-parent';
import State from './state';

class App extends React.Component {
  render() {
    return (
      <div>
        <a href="/">
          <button>Home</button>
        </a>
        <BrowserRouter>
          <Switch>
            <Route exact path="/" component={Home} />
            <Route exact path="/mapKey" component={MapKey} />
            <Route
              exact
              path="/functionalComponent"
              component={FunctionalComponent}
            />
            <Route exact path="/classComponent" component={ClassComponent} />
            <Route exact path="/state" component={State} />
            <Route exact path="/props" component={PropsParent} />
            <Route
              exact
              path="/conditionalRendering"
              component={ConditionalRendering}
            />
            <Route
              exact
              path="/componentLifeCycle"
              component={ComponentLifeCycle}
            />
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
