import React, { useReducer } from "react";

const initialState = { name: "Maria" };

function reducer(state, action) {
    switch (action.type) {
        case "changeName":
            return { name: "Jimmy" };
        case "resetName":
            return { name: "Maria" };
        default:
            throw new Error();
    }
}

export default function UseReducer() {
    const [state, dispatch] = useREducer(reducer, initialState);

    return ( <
        div >
        <
        h1 > useReducer: < /h1> <
        p > name: { state.name } < /p> <
        button onClick = {
            () => dispatch({ type: "changeName" }) } >
        change name <
        /button> <
        button onClick = {
            () => dispatch({ type: "resetName" }) } >
        reset name <
        /button> <
        /div>
    );
}
