import React from 'react';

class ComponentDidMount extends React.Component {
  constructor(props) {
    super(props);
    this.state = { counter: 0 };
  }

  componentWillMount() {
    this.setState({ counter: (counter += 1) });
  }

  componentDidMount() {
    this.setState({ counter: (counter += 1) });
  }

  render() {
    return (
      <div>
        <h1>componentDidMount:</h1>
        {this.state.counter}
      </div>
    );
  }
}

export default ComponentDidMount;
