import React from 'react';

const OptionOne = () => <h1>Hello 1</h1>;
const OptionTwo = () => <h1>Hello 2</h1>;

class ConditionalRendering extends React.Component {
  showMessage() {
    if (true) {
      return <OptionOne />;
    } else {
      return <OptionTwo />;
    }
  }

  render() {
    return <div>{this.showMessage()}</div>;
  }
}

export default ConditionalRendering;
