import React from 'react';

import ComponentDidMount from './component-did-mount';
import ComponentDidUpdate from './component-did-update';
import ComponentWillUnmount from './component-will-unmount';
import GetDerivedStateFromProps from './get-derived-state-from-props';
import GetSnapshotBeforeUpdate from './get-snapshot-before-update';
import ShouldComponentUpdate from './should-component-update';

class ComponentLifeCycle extends React.Component {
  constructor(props) {
    super(props);
    this.state = { date: new Date(), counter: 0 };
    this.increase = this.increase1.bind(this);
  }

  componentDidMount() {
    this.timer = setInterval(
      () =>
        this.setState({
          date: new Date(),
        }),
      1000
    );
  }

  increase() {
    this.setState({ counter: this.state.counter + 1 });
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  render() {
    const hr = {
      margin: "2rem 0",
    };

    return (
      <div>
        <ComponentDidMount />
        <hr style={hr} />
        <ComponentDidUpdate props={this.state.date.toLocaleTimeString()} />
        <hr style={hr} />
        <ComponentWillUnmount />
        <hr style={hr} />
        <ShouldComponentUpdate
          value={this.state.counter}
          onClick={this.increase}
        />
        <hr style={hr} />
        <GetDerivedStateFromProps props="props" />
        <hr style={hr} />
        <GetSnapshotBeforeUpdate props={this.state.date.toLocaleTimeString()} />
      </div>
    );
  }
}

export default ComponentLifeCycle;
