import React from 'react';

class Listener extends React.Component {
  render() {
    return (
      <div style={this.props.main}>
        component taking in other component through render props
      </div>
    );
  }
}

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      clickCount: 0,
      border: "",
      backgroundColor: "",
      padding: "",
    };
  }

  handleClick() {
    this.setState({
      border: "1px solid black",
      backgroundColor: "#0ff",
      padding: "1rem",
    });
  }

  render() {
    return (
      <div>
        <div style={this.state}>main component</div>
        <button onClick={this.handleClick} style={{ marginTop: "1rem" }}>
          set the state
        </button>
        {this.props.render(this.state)}
      </div>
    );
  }
}

export default class RenderProps extends React.Component {
  render() {
    return (
      <div>
        <h2>Render props:</h2>
        <Main render={(main) => <Listener main={main} />} />
      </div>
    );
  }
}
